$(function(){
    var TxtRotate = function(el, toRotate, period) {
        this.toRotate = toRotate;
        this.el = el;
        this.loopNum = 0;
        this.period = parseInt(period, 10) || 2000;
        this.txt = '';
        this.tick();
        this.isDeleting = false;
      };
      
      TxtRotate.prototype.tick = function() {
        var i = this.loopNum % this.toRotate.length;
        var fullTxt = this.toRotate[i];
      
        if (this.isDeleting) {
          this.txt = fullTxt.substring(0, this.txt.length - 1);
        } else {
          this.txt = fullTxt.substring(0, this.txt.length + 1);
        }
      
        this.el.innerHTML = '<span class="wrap">'+this.txt+'</span>';
      
        var that = this;
        var delta = 200 - Math.random() * 100;
      
        if (this.isDeleting) { delta /= 2; }
      
        if (!this.isDeleting && this.txt === fullTxt) {
          delta = this.period;
          this.isDeleting = true;
        } else if (this.isDeleting && this.txt === '') {
          this.isDeleting = false;
          this.loopNum++;
          delta = 100;
        }
      
        setTimeout(function() {
          that.tick();
        }, delta);
      };
      
      window.onload = function() {
        var elements = document.getElementsByClassName('txt-rotate');
        for (var i=0; i<elements.length; i++) {
          var toRotate = elements[i].getAttribute('data-rotate');
          var period = elements[i].getAttribute('data-period');
          if (toRotate) {
            new TxtRotate(elements[i], JSON.parse(toRotate), period);
          }
        }
        // INJECT CSS
        var css = document.createElement("style");
        css.type = "text/css";
        css.innerHTML = ".txt-rotate > .wrap { border-right: 0em solid #666 ; }";
        document.body.appendChild(css);
      };
})
$(function(){
	document.querySelectorAll('.footer-btn').forEach(item =>{
		item.addEventListener('click', () => {
			document.querySelectorAll('.menu_mobile').forEach(x1 =>{
				x1.classList.add('active');
			});
			document.querySelectorAll('.close-menu').forEach(x2 =>{
				x2.classList.add('close-menu-active');
			});
		});
	});
		// а тут мы вешаем на кнопку close-menu событие которое пр кликена кнопку
		// удаляет menu_mobile  класс  active
		document.querySelectorAll('.close-menu').forEach(item =>{
			item.addEventListener('click', () => {
				document.querySelectorAll('.menu_mobile').forEach( x3 =>{
					x3.classList.remove('active');
				});
				document.querySelectorAll('.close-menu').forEach(x4 =>{
					x4.classList.classList.remove('close-menu-active');
				});
			});
		});
		// document.querySelectorAll('.close-menu').addEventListener('click', () => {
		// document.querySelectorAll('.menu_mobile').classList.remove('active');
		// document.querySelectorAll('.close-menu').classList.remove('close-menu-active')
		// });
});
